import React, {useEffect} from 'react';
import './menudisplay.css';

import {
	MenuDisplayContainer,
	MenuList,
	MenuItem,
	MenuPrice
} from './MenuElements';

const MenuDisplay = ({info}) =>{
	console.log("from display ", info);

// 	menu_item_description: "carna asada tacos"
//  menu_item_price: 1.99
//  menu_item_title: "tacos"

	return ( 

		<MenuDisplayContainer>
				{info.map(item =>(
				<MenuList>
					<MenuItem>
						{item.menu_item_title}
					</MenuItem>
					<MenuDescription>
						${item.menu_item_description}
					</MenuDescription>
					<MenuPrice>
						${item.menu_item_price}
					</MenuPrice>
				</MenuList>	
				))}
		</MenuDisplayContainer>
	)
}




export default MenuDisplay;
