import React, {useEffect, useState} from 'react';
import {Button} from '../ButtonElement';
import MenuDisplay from './MenuDisplay';
import { 
	MenuContainer,
	MenuWrapper,
	MenuRow,
	Column1,
	Column2,
	TextWrapper,
	TopLine,
	Heading,
	Subtitle,
	BtnWrap,
	ImgWrap,
	Img
} from './MenuElements';

const Menu = ({
	lightBg,
	id,
	imgStart,
	topLine,
	lightText,
	headLine,
	darkText,
	description,
	buttonLabel,
	img,
	alt,
	primary,
	dark,
	dark2
}) =>{

	const[displayInfo, setDisplayInfo] = useState([])

	useEffect(()=>{


	}, displayInfo);

	let menuItem = [
		"Lunch",
		"Tacos",
		"Catering",
		"Meats"
	]

	let menuItemInfo = {
		Lunch: [
			{item: "Lunch Taco", price: 2.99},
			{item: "Lunch Burrito", price: 5.99},
			{item: "Lunch Nachos", price: 5.99}
		],
		Taco: [
			{item: "Taco Taco", price: 2.99},
			{item: "Taco Burrito", price: 5.99},
			{item: "Taco Nachos", price: 5.99}
		],
		Catering: [
			{item: "Catering Taco", price: 2.99},
			{item: "Catering Burrito", price: 5.99},
			{item: "Catering Nachos", price: 5.99}
		]
	}

	const handleClick = e =>{
		e.preventDefault();
		console.log(e.target.value, " from handle click ")
		setDisplayInfo(menuItemInfo[e.target.value]);
	}

	return (
		<>
			<MenuContainer lightBg={lightBg} id={id}>
				<MenuWrapper>
					<MenuRow imgStart={imgStart}>

						<Column1>
							<h2 style={{color: "black"}}>Menu Items</h2>
							<TextWrapper>
								<TopLine>{topLine}</TopLine>
								<Heading lightText={lightText}>{headLine}</Heading>
								<Subtitle darkText={darkText}>{description}</Subtitle>  
								<BtnWrap>
									<Button 
										to="home"
										smooth={true}
										duration={500}
										spy={true}
										exact="true"
										offset={-80}
										primary={primary ? 1 : 0}
										dark={dark ? 1 : 0}
										dark2={dark2 ? 1: 0}
									>
										{buttonLabel}</Button>
								</BtnWrap>
							</TextWrapper>
						</Column1>
						<Column2>
							<TextWrapper>
								<TopLine>Menu</TopLine>
								{
									menuItem.map(item =>(
										<button 
											onClick={e => handleClick(e)} 
											value={item}
											>
											{item}
										</button>
									))
								}

								{console.log(" menu ", displayInfo)}
								<MenuDisplay info={displayInfo} />

							</TextWrapper>


						</Column2>
					</MenuRow>
				</MenuWrapper>
			</MenuContainer>
		</>
	)
}

export default Menu;
